import Foundation

extension String {
    func index(from: Int) -> Index {
        return self.index(startIndex, offsetBy: from)
    }
    
    func substring(from i: Int) -> String {
        let startIndex = index(from: i)
        let endIndex = index(startIndex, offsetBy: 5)
        return substring(with: startIndex..<endIndex)
    }
}

func stringSum(string s: String) -> Int {
    let sum = s.characters.flatMap { Int(String($0)) }.reduce(0) {$0 + $1}
    return sum
}

func stringMulti(string s: String) -> Int {
    let mult = s.characters.flatMap { Int(String($0)) }.reduce(1) {$0 * $1}
    return mult
}

let number = ""

let length = number.characters.count - 5
var maxSum = 0
var maxArray = ""

for i in 0...length {
    let subString = number.substring(from: i)
    if subString.contains("0") {
        continue
    }
    let total = stringSum(string: subString)
    
    if maxSum < total {
        maxSum = total
        maxArray = subString
    }
}

print("maxArray:\(maxArray) maxSum: \(maxSum) maxMult: \(stringMulti(string: maxArray))")

